import React, {useState, useEffect, useRef} from "react";
import ReactDOM from "react-dom";
import axios from "axios";
import {useNavigate} from "react-router-dom";
import TextField from "@mui/material/TextField";
import SearchIcon from "@mui/icons-material/Search";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import Avatar from "@mui/material/Avatar";
import ListItemText from "@mui/material/ListItemText";
import Paper from "@mui/material/Paper";
import IconButton from "@mui/material/IconButton";
import InputAdornment from "@mui/material/InputAdornment";

interface Product {
    id: number;
    name: string;
    image: string;
}

const SearchBar: React.FC = () => {
    const [query, setQuery] = useState("");
    const [suggestions, setSuggestions] = useState<Product[]>([]);
    const [showDropdown, setShowDropdown] = useState(false);
    const typingTimeoutRef = useRef<NodeJS.Timeout | null>(null);
    const navigate = useNavigate();
    const searchBarRef = useRef<HTMLDivElement | null>(null);

    useEffect(() => {
        if (query.length >= 3) {
            if (typingTimeoutRef.current) clearTimeout(typingTimeoutRef.current);

            typingTimeoutRef.current = setTimeout(async () => {
                try {
                    console.log("Requête envoyée avec :", query);
                    const response = await axios.get(`http://localhost:8080/product/search?keyword=${query}`);
                    console.log("Réponse reçue :", response.data);

                    if (response.data && response.data.length > 0) {
                        setSuggestions(response.data);
                        setShowDropdown(true);
                    } else {
                        setSuggestions([]);
                        setShowDropdown(false);
                    }
                } catch (error) {
                    console.error("Erreur lors de la récupération des produits :", error);
                    setSuggestions([]);
                    setShowDropdown(false);
                }
            }, 2000);
        } else {
            setSuggestions([]);
            setShowDropdown(false);
        }
    }, [query]);

    const handleProductClick = (id: number) => {
        setShowDropdown(false);
        setTimeout(() => navigate(`/product-details/${id}`), 100);
    };

    return (
        <div className="relative flex items-center w-full max-w-lg" ref={searchBarRef}>
            <TextField
                fullWidth
                variant="outlined"
                placeholder="Rechercher une plante..."
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                InputProps={{
                    startAdornment: (
                        <InputAdornment position="start">
                            <IconButton>
                                <SearchIcon/>
                            </IconButton>
                        </InputAdornment>
                    ),
                }}
                sx={{
                    width: 250,
                    "& .MuiOutlinedInput-root": {
                        color: "white", // texte en blanc
                        backgroundColor: "#386641", // fond initial
                        transition: "background-color 0.3s ease",
                        "&:hover": {
                            backgroundColor: "#386641" // fond plus foncé au survol
                        },
                        "&.Mui-focused": {
                            backgroundColor: "#4a8a51" // fond encore plus foncé au focus
                        },
                        "& fieldset": {
                            borderColor: "rgba(255,255,255,0.5)"
                        },
                        "&:hover fieldset": {
                            borderColor: "rgba(255,255,255,0.7)"
                        },
                        "&.Mui-focused fieldset": {
                            borderColor: "white"
                        }
                    },
                    "& .MuiInputAdornment-root .MuiIconButton-root": {
                        color: "white" // icône en blanc
                    }
                }}
            />
            {showDropdown && suggestions.length > 0 && ReactDOM.createPortal(
                <div style={{position: "fixed", top: "65px", right: "370px", zIndex: 1000}}>
                    <Paper
                        className="w-96 max-h-60 overflow-auto shadow-lg bg-white border border-gray-300"
                        style={{width: '600px', overflowY: "auto", borderRadius: "8px"}}
                    >
                        <List>
                            {suggestions.map((product) => (
                                <ListItem
                                    key={product.id}
                                    onClick={() => handleProductClick(product.id)}
                                    component="li"
                                    style={{cursor: "pointer"}}
                                >
                                    <ListItemAvatar>
                                        <Avatar src={product.image} alt={product.name}/>
                                    </ListItemAvatar>
                                    <ListItemText primary={product.name}/>
                                </ListItem>
                            ))}
                        </List>
                    </Paper>
                </div>,
                document.body
            )}
        </div>
    );
};

export default SearchBar;
