import {useEffect, useState} from "react";
import {fetchIndoorPlants} from "../../services/productService";
import {useNavigate} from "react-router-dom";
import ProductCarousel from "../landingPage/carousels/ProductCarousel";


// ðŸ”¹ Type des donnÃ©es
type Plant = {
    id: number;
    name: string;
    image: string;
    description: string;
    price: number;
};

function HouseplantsCarousel() {
    const [plantsData, setPlantsData] = useState<Plant[]>([]);
    const navigate = useNavigate();

    useEffect(() => {
        async function loadPlants() {
            try {
                const data = await fetchIndoorPlants();
                setPlantsData(data);
            } catch (error) {
                console.error("Erreur lors du chargement des plantes d'intÃ©rieur :", error);
            }
        }

        loadPlants();
    }, []);

    // ðŸ”¥ Garde exactement ta logique de transformation
    const indoorPlants = plantsData.map((plant) => ({
        id: plant.id,
        image: plant.image || "https://via.placeholder.com/250", // âœ… Image par dÃ©faut si null
        name: plant.name,
        description: plant.description,
        price: plant.price.toFixed(2) + "â‚¬", // ðŸ”¥ Convertir price en string
    }));

    return (
        <section style={{position: "relative", marginTop: "40px"}}>
            {/* ðŸ”¥ Utilisation de ton `ProductCarousel` avec les plantes filtrÃ©es */}
            <ProductCarousel products={indoorPlants}/>

            {/* ðŸ”¥ Lien "Voir plus" qui redirige avec le filtre "Plantes d'intÃ©rieur" activÃ© */}
            <div style={{position: "absolute", right: "3.5%"}}>
                <span
                    style={{cursor: "pointer", color: "#1976d2", fontWeight: "bold"}}
                    onClick={() => navigate("/products?filter=indoor")}
                >
                    Voir plus
                </span>
            </div>
        </section>
    );
}

export default HouseplantsCarousel;
