import React, {useState, useEffect, useRef} from "react";
import axios from "axios";
import {useNavigate} from "react-router-dom";
import TextField from "@mui/material/TextField";
import SearchIcon from "@mui/icons-material/Search";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import Avatar from "@mui/material/Avatar";
import ListItemText from "@mui/material/ListItemText";
import Paper from "@mui/material/Paper";
import IconButton from "@mui/material/IconButton";

interface Product {
    id: number;
    name: string;
    image: string;
}

const SearchBar: React.FC = () => {
    const [query, setQuery] = useState("");
    const [suggestions, setSuggestions] = useState<Product[]>([]);
    const [showDropdown, setShowDropdown] = useState(false);
    const typingTimeoutRef = useRef<NodeJS.Timeout | null>(null);
    const navigate = useNavigate();
    const searchBarRef = useRef<HTMLDivElement | null>(null);

    useEffect(() => {
        if (query.length >= 3) {
            if (typingTimeoutRef.current) clearTimeout(typingTimeoutRef.current);

            typingTimeoutRef.current = setTimeout(async () => {
                try {
                    console.log("Requête envoyée avec :", query);
                    const response = await axios.get(`http://localhost:8080/product/search?keyword=${query}`);
                    console.log("Réponse reçue :", response.data);

                    if (response.data && response.data.length > 0) {
                        setSuggestions(response.data);
                        setShowDropdown(true);
                    } else {
                        setSuggestions([]);
                        setShowDropdown(false);
                    }
                } catch (error) {
                    console.error("Erreur lors de la récupération des produits :", error);
                    setSuggestions([]);
                    setShowDropdown(false);
                }
            }, 2000);
        } else {
            setSuggestions([]);
            setShowDropdown(false);
        }
    }, [query]);

    const handleProductClick = (id: number) => {
        setShowDropdown(false);
        setTimeout(() => navigate(`/product-details/${id}`), 100);
    };

    return (
        <div className="relative w-full" ref={searchBarRef}>
            <div className="flex items-center border rounded-md px-2 relative z-50">
                <IconButton>
                    <SearchIcon/>
                </IconButton>
                <TextField
                    fullWidth
                    variant="standard"
                    placeholder="Rechercher une plante..."
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                />
            </div>
            {showDropdown && suggestions.length > 0 && (
                <Paper
                    className="absolute z-50 w-96 max-h-60 overflow-auto shadow-lg bg-white border border-gray-300"
                    style={{
                        top: "100%",
                        left: 0,
                        position: "fixed",
                        width: "300px",
                        maxWidth: "400px",
                        maxHeight: "300px",
                        overflowY: "auto",
                        borderRadius: "8px"
                    }}
                >
                    <List>
                        {suggestions.map((product) => (
                            <ListItem
                                key={product.id}
                                onClick={() => handleProductClick(product.id)}
                                component="li"
                                style={{cursor: "pointer"}}
                            >
                                <ListItemAvatar>
                                    <Avatar src={product.image} alt={product.name}/>
                                </ListItemAvatar>
                                <ListItemText primary={product.name}/>
                            </ListItem>
                        ))}
                    </List>
                </Paper>
            )}
        </div>
    );
};

export default SearchBar;
