import {useEffect, useState} from "react";
import {fetchProducts} from "../../services/productService"; // üî• Remplacement des dataFakes par l'API
import "../styles/AllProducts.css";
import GridProduct from "../../components/allProductsPage/GridProduct";


type Product = {
    id: number;
    name: string;
    description: string;
    category: string;
    price: number;
    image: string;
};

function AllProducts() {
    const [products, setProducts] = useState([]); // Stocke tous les produits
    const [filteredProducts, setFilteredProducts] = useState([]); // Stocke les produits filtr√©s
    const [searchTerm, setSearchTerm] = useState(""); // G√®re la recherche
    const [selectedCategory, setSelectedCategory] = useState(""); // G√®re le filtre par cat√©gorie

    // Chargement des produits depuis l'API
    useEffect(() => {
        async function loadProducts() {
            try {
                const data = await fetchProducts(); // üî• Appelle l'API Spring Boot
                setProducts(data);
                setFilteredProducts(data); // Initialise les produits affich√©s
            } catch (error) {
                console.error("Erreur lors du chargement des produits :", error);
            }
        }

        loadProducts();
    }, []);

    // G√®re la recherche et le filtrage
    useEffect(() => {
        let filtered = products;

        if (searchTerm) {
            filtered = filtered.filter(product =>
                product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                product.description.toLowerCase().includes(searchTerm.toLowerCase())
            );
        }

        if (selectedCategory) {
            filtered = filtered.filter(product => product.category === selectedCategory);
        }

        setFilteredProducts(filtered);
    }, [searchTerm, selectedCategory, products]);

    return (
        <div className="all-products">
            <h2>Nos Plantes</h2>

            {/* Barre de recherche */}
            <input
                type="text"
                placeholder="Rechercher une plante..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="search-bar"
            />

            {/* Filtres */}
            <div className="filters">
                <select onChange={(e) => setSelectedCategory(e.target.value)} value={selectedCategory}>
                    <option value="">Toutes les cat√©gories</option>
                    <option value="int√©rieur">Plantes d'int√©rieur</option>
                    <option value="ext√©rieur">Plantes d'ext√©rieur</option>
                    <option value="cactus">Cactus & Succulentes</option>
                </select>
            </div>

            {/* Affichage des produits via GridProduct */}
            <GridProduct products={filteredProducts}/>
        </div>
    );
}

export default AllProducts;
