import * as React from 'react';
import {Link, useNavigate} from 'react-router-dom';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import Menu from '@mui/material/Menu';
import Container from '@mui/material/Container';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import Tooltip from '@mui/material/Tooltip';
import MenuItem from '@mui/material/MenuItem';
import AdbIcon from '@mui/icons-material/Adb';
import SearchBar from '../../components/header/SearchBar';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';

const pages = [
    {name: "Accueil", path: "/"},
    {name: "Produits", path: "/products"},
    {name: "Panier", path: "/cart"},
    {name: "Admin", path: "/admin"},
    {name: "Connexion", path: "/login"},
];

export default function Header() {
    const navigate = useNavigate();
    const [anchorElUser, setAnchorElUser] = React.useState<null | HTMLElement>(null);

    const handleOpenUserMenu = (event: React.MouseEvent<HTMLElement>) => {
        setAnchorElUser(event.currentTarget);
    };

    const handleCloseUserMenu = () => {
        setAnchorElUser(null);
    };

    const handleProductsClick = () => {
        navigate("/products", {
            state: {
                filters: {
                    indoor: true,
                    outdoor: true,
                    easycare: true,
                },
            },
        });
    };

    return (
        <AppBar position="static" sx={{backgroundColor: '#353b2b'}}>
            <Container maxWidth="xl">
                <Toolbar disableGutters>
                    <AdbIcon sx={{display: {xs: "none", md: "flex"}, mr: 1}}/>
                    <Typography
                        variant="h6"
                        noWrap
                        component="a"
                        sx={{
                            mr: 2,
                            display: {xs: "none", md: "flex"},
                            fontFamily: "monospace",
                            fontWeight: 700,
                            letterSpacing: ".3rem",
                            color: "inherit",
                            textDecoration: "none",
                        }}
                    >
                        LOGO
                    </Typography>

                    <Box sx={{flexGrow: 1, display: "flex", ml: 3}}>
                        {pages.map((page) =>
                            page.name === "Produits" ? (
                                <Button
                                    key={page.name}
                                    onClick={handleProductsClick}
                                    sx={{color: "white", mx: 1}}
                                >
                                    {page.name}
                                </Button>
                            ) : (
                                <Button
                                    key={page.name}
                                    component={Link}
                                    to={page.path}
                                    sx={{color: "white", mx: 1}}
                                >
                                    {page.name}
                                </Button>
                            )
                        )}
                    </Box>

                    <div className="flex items-center w-1/3 max-w-md" style={{marginLeft: "-20px"}}>
                        <SearchBar/>
                    </div>

                    <Box sx={{flexGrow: 0, display: "flex", alignItems: "center", marginLeft: "auto"}}>
                        <Tooltip title="Paramètres">
                            <IconButton onClick={handleOpenUserMenu} sx={{p: 0, mr: 2}}>
                                <Avatar alt="User Profile" src="/static/images/avatar/2.jpg"/>
                            </IconButton>
                        </Tooltip>


                        <IconButton component={Link} to="/cart" sx={{color: "white"}}>
                            <ShoppingCartIcon/>
                        </IconButton>
                    </Box>
                </Toolbar>
            </Container>
        </AppBar>
    );
}
