import React, {useState, useEffect, useRef} from "react";
import ReactDOM from "react-dom";
import axios from "axios";
import {useNavigate} from "react-router-dom";
import TextField from "@mui/material/TextField";
import SearchIcon from "@mui/icons-material/Search";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import Avatar from "@mui/material/Avatar";
import ListItemText from "@mui/material/ListItemText";
import Paper from "@mui/material/Paper";
import IconButton from "@mui/material/IconButton";
import {InputAdornment} from "@mui/material";


interface Product {
    id: number;
    name: string;
    image: string;
}

// Nouvelle fonction pour rechercher des produits par mot-clé
async function fetchProductsByKeyword(keyword: string): Promise<Product[]> {
    try {
        const response = await fetch(
            `http://localhost:8080/product/search?keyword=${encodeURIComponent(keyword)}`
        );
        if (!response.ok) {
            throw new Error("Erreur lors de la récupération des produits");
        }
        return await response.json();
    } catch (error) {
        console.error("Erreur API :", error);
        return [];
    }
}

const SearchBar: React.FC = () => {
    const [query, setQuery] = useState("");
    const [suggestions, setSuggestions] = useState<Product[]>([]);
    const [showDropdown, setShowDropdown] = useState(false);
    const typingTimeoutRef = useRef<NodeJS.Timeout | null>(null);
    const navigate = useNavigate();
    const searchBarRef = useRef<HTMLDivElement | null>(null);

    // Effet pour déclencher la recherche avec un debounce de 300ms (si la saisie fait au moins 3 caractères)
    useEffect(() => {
        if (query.length >= 3) {
            if (typingTimeoutRef.current) clearTimeout(typingTimeoutRef.current);

            typingTimeoutRef.current = setTimeout(async () => {
                const data = await fetchProductsByKeyword(query);
                if (data && data.length > 0) {
                    setSuggestions(data);
                    setShowDropdown(true);
                } else {
                    setSuggestions([]);
                    setShowDropdown(false);
                }
            }, 300);
        } else {
            setSuggestions([]);
            setShowDropdown(false);
        }
    }, [query]);

    // Ferme le menu déroulant si l'utilisateur clique en dehors de la SearchBar
    useEffect(() => {
        const handleClickOutside = (event: MouseEvent) => {
            if (searchBarRef.current && !searchBarRef.current.contains(event.target as Node)) {
                setShowDropdown(false);
            }
        };

        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, []);

    // Redirige vers la page ProductDetails lors du clic sur un produit
    const handleProductClick = (id: number) => {
        if (!id) {
            console.error("ID du produit invalide !");
            return;
        }
        setShowDropdown(false);
        navigate(`/product-details/${id}`);
    };

    return (
        <div className="searchbar-wrapper" ref={searchBarRef}>
            <div className="searchbar-container">
                <TextField
                    fullWidth
                    variant="outlined"
                    placeholder="Rechercher une plante..."
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    InputProps={{
                        startAdornment: (
                            <InputAdornment position="start">
                                <IconButton>
                                    <SearchIcon/>
                                </IconButton>
                            </InputAdornment>
                        ),
                    }}
                    sx={{width: 250, height: 40}}
                />
            </div>
            {/* Affichage du menu déroulant des suggestions */}
            {showDropdown && suggestions.length > 0 &&
                ReactDOM.createPortal(
                    <div style={{position: "fixed", top: "80px", right: "200px", zIndex: 1000}}>
                        <Paper className="dropdown-menu"
                               style={{maxWidth: "400px", overflowY: "auto", borderRadius: "8px"}}>
                            <List>
                                {suggestions.map((product) => (
                                    <ListItem
                                        key={product.id}
                                        onClick={() => handleProductClick(product.id)}
                                        component="li"
                                        style={{cursor: "pointer"}}
                                    >
                                        <ListItemAvatar>
                                            <Avatar src={product.image || "https://via.placeholder.com/150"}
                                                    alt={product.name}/>
                                        </ListItemAvatar>
                                        <ListItemText primary={product.name}/>
                                    </ListItem>
                                ))}
                            </List>
                        </Paper>
                    </div>,
                    document.body
                )}
        </div>
    );
};

export default SearchBar;
