import React, {useState, useEffect} from "react";
import axios from "axios";

interface Product {
    id: number;
    name: string;
    image: string;
}

const SearchBar: React.FC = () => {
    const [query, setQuery] = useState("");
    const [suggestions, setSuggestions] = useState<Product[]>([]);
    const [showDropdown, setShowDropdown] = useState(false);

    useEffect(() => {
        if (query.length >= 3) {
            axios
                .get(`/product/search?keyword=${query}`)
                .then((response) => {
                    setSuggestions(response.data);
                    setShowDropdown(true);
                })
                .catch((error) => console.error("Erreur lors de la r√©cup√©ration des produits :", error));
        } else {
            setShowDropdown(false);
        }
    }, [query]);

    return (
        <div className="relative w-full">
            <div className="relative">
                <input
                    type="text"
                    placeholder="Rechercher une plante..."
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring focus:ring-green-400 pl-10"
                />
                <span className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-500">
          üîç
        </span>
            </div>
            {showDropdown && (
                <ul className="absolute z-10 w-full bg-white border border-gray-200 rounded-md shadow-lg mt-1 max-h-60 overflow-auto">
                    {suggestions.length > 0 ? (
                        suggestions.map((product) => (
                            <li
                                key={product.id}
                                className="flex items-center px-4 py-2 cursor-pointer hover:bg-green-100"
                                onClick={() => setQuery(product.name)}
                            >
                                <img src={product.image} alt={product.name} className="w-8 h-8 rounded-full mr-2"/>
                                {product.name}
                            </li>
                        ))
                    ) : (
                        <li className="px-4 py-2 text-gray-500">Aucun produit trouv√©</li>
                    )}
                </ul>
            )}
        </div>
    );
};

export default SearchBar;
