import React, {useState, useEffect} from "react";
import axios from "axios";
import TextField from "@mui/material/TextField";
import SearchIcon from "@mui/icons-material/Search";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import Avatar from "@mui/material/Avatar";
import ListItemText from "@mui/material/ListItemText";
import Paper from "@mui/material/Paper";
import IconButton from "@mui/material/IconButton";

type Product = {
    id: number;
    name: string;
    image: string;
};

const SearchBar: React.FC = () => {
    const [query, setQuery] = useState("");
    const [suggestions, setSuggestions] = useState<Product[]>([]);
    const [showDropdown, setShowDropdown] = useState(false);

    useEffect(() => {
        if (query.length >= 3) {
            axios
                .get(`/product/search?keyword=${query}`)
                .then((response) => {
                    setSuggestions(response.data);
                    setShowDropdown(true);
                })
                .catch((error) => console.error("Erreur lors de la récupération des produits :", error));
        } else {
            setShowDropdown(false);
        }
    }, [query]);

    return (
        <div className="relative w-full">
            <div className="flex items-center border rounded-md px-2">
                <IconButton>
                    <SearchIcon/>
                </IconButton>
                <TextField
                    fullWidth
                    variant="standard"
                    placeholder="Rechercher une plante..."
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                />
            </div>
            {showDropdown && (
                <Paper className="absolute z-10 w-full mt-1 max-h-60 overflow-auto">
                    <List>
                        {suggestions.length > 0 ? (
                            suggestions.map((product) => (
                                <ListItem key={product.id} onClick={() => setQuery(product.name)} component="div">
                                    <ListItemAvatar>
                                        <Avatar src={product.image} alt={product.name}/>
                                    </ListItemAvatar>
                                    <ListItemText primary={product.name}/>
                                </ListItem>
                            ))
                        ) : (
                            <ListItem component="div">
                                <ListItemText primary="Aucun produit trouvé"/>
                            </ListItem>
                        )}
                    </List>
                </Paper>
            )}
        </div>
    );
};

export default SearchBar;
