import {useEffect, useState} from "react";
import {fetchIndoorPlants} from "../../services/productService";

type Plant = {
    id: number;
    name: string;
    image: string;
};

function HouseplantsCarousel() {
    const [plants, setPlants] = useState<Plant[]>([]);

    useEffect(() => {
        async function loadPlants() {
            try {
                const data = await fetchIndoorPlants();
                setPlants(data);
            } catch (error) {
                console.error("Erreur lors du chargement des plantes d'intÃ©rieur :", error);
            }
        }

        loadPlants();
    }, []);

    return (
        <div className="houseplants-carousel">
            <h2>ðŸŒ¿ Plantes d'intÃ©rieur</h2>
            <div className="carousel-container">
                {plants.length > 0 ? (
                    plants.map(plant => (
                        <div key={plant.id} className="carousel-item">
                            <img src={plant.image} alt={plant.name}/>
                            <p>{plant.name}</p>
                        </div>
                    ))
                ) : (
                    <p>Chargement des plantes...</p>
                )}
            </div>
        </div>
    );
}

export default HouseplantsCarousel;
