import React, {useState, useEffect, useRef} from "react";
import ReactDOM from "react-dom";
import axios from "axios";
import {useNavigate} from "react-router-dom";
import TextField from "@mui/material/TextField";
import SearchIcon from "@mui/icons-material/Search";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import Avatar from "@mui/material/Avatar";
import ListItemText from "@mui/material/ListItemText";
import Paper from "@mui/material/Paper";
import IconButton from "@mui/material/IconButton";
import {InputAdornment} from "@mui/material";

interface Product {
    id: number;
    name: string;
    image: string;
}

const SearchBar: React.FC = () => {
    const [query, setQuery] = useState(""); // État pour stocker la requête de recherche
    const [suggestions, setSuggestions] = useState<Product[]>([]); // Liste des suggestions affichées
    const [showDropdown, setShowDropdown] = useState(false); // Contrôle l'affichage du menu déroulant
    const typingTimeoutRef = useRef<NodeJS.Timeout | null>(null);
    const navigate = useNavigate();
    const searchBarRef = useRef<HTMLDivElement | null>(null);

    // Effet pour gérer la recherche en différé après 2 secondes
    useEffect(() => {
        if (query.length >= 3) {
            if (typingTimeoutRef.current) clearTimeout(typingTimeoutRef.current);

            typingTimeoutRef.current = setTimeout(async () => {
                try {
                    console.log("Requête envoyée avec :", query);
                    const response = await axios.get(`http://localhost:8080/product/search?keyword=${query}`);
                    console.log("Réponse reçue :", response.data);

                    if (response.data && response.data.length > 0) {
                        setSuggestions(response.data);
                        setShowDropdown(true);
                    } else {
                        setSuggestions([]);
                        setShowDropdown(false);
                    }
                } catch (error) {
                    console.error("Erreur lors de la récupération des produits :", error);
                    setSuggestions([]);
                    setShowDropdown(false);
                }
            }, 2000);
        } else {
            setSuggestions([]);
            setShowDropdown(false);
        }
    }, [query]);

    // Effet pour fermer le menu lorsqu'on clique en dehors de la SearchBar
    useEffect(() => {
        function handleClickOutside(event: MouseEvent) {
            if (searchBarRef.current && !searchBarRef.current.contains(event.target as Node)) {
                setShowDropdown(false);
            }
        }

        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, []);

    // Fonction appelée lorsqu'un produit est sélectionné
    const handleProductClick = (id: number) => {
        setShowDropdown(false);
        navigate(`/product-details/${id}`);
    };

    return (
        <div className="searchbar-wrapper" ref={searchBarRef}>
            {/* Conteneur de la SearchBar */}
            <div className="searchbar-container">
                <TextField
                    fullWidth
                    variant="outlined"
                    placeholder="Rechercher une plante..."
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    InputProps={{
                        startAdornment: (
                            <InputAdornment position="start">
                                <IconButton>
                                    <SearchIcon/>
                                </IconButton>
                            </InputAdornment>
                        ),
                    }}
                    sx={{width: 250, height: 40}}
                />
            </div>
            {/* Affichage du menu déroulant des suggestions */}
            {showDropdown && suggestions.length > 0 && ReactDOM.createPortal(
                <div style={{position: "fixed", top: "80px", right: "200px", zIndex: 1000}}>
                    <Paper
                        className="dropdown-menu"
                        style={{maxWidth: "400px", overflowY: "auto", borderRadius: "8px"}}
                    >
                        <List>
                            {suggestions.map((product) => (
                                <ListItem
                                    key={product.id}
                                    onClick={() => handleProductClick(product.id)}
                                    component="li"
                                    style={{cursor: "pointer"}}
                                >
                                    <ListItemAvatar>
                                        <Avatar src={product.image} alt={product.name}/>
                                    </ListItemAvatar>
                                    <ListItemText primary={product.name}/>
                                </ListItem>
                            ))}
                        </List>
                    </Paper>
                </div>,
                document.body
            )}
        </div>
    );
};

export default SearchBar;
